import React, {useState, useEffect} from 'react';
import { useLocation } from 'react-router-dom';
import {listPosts} from '../graphql/queries'
import Amplify, { API, graphqlOperation } from 'aws-amplify'
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { Carousel } from 'react-responsive-carousel';

function Details() {
    const location = useLocation();
    const homeid = location.state.homeid;
    const [posts, setPosts] = useState();
    useEffect ( () => {
        const fetchPosts = async () => {
            try{
                const postsResult = await API.graphql(
                    graphqlOperation(listPosts)
                )

                setPosts(postsResult.data.listPosts.items);
            } catch (e){
                console.log(e);
            }
        }

        fetchPosts();
    })
    if(posts === undefined){
        return null;
    }

    const post = posts.find(place => place.id === homeid);
    const images = ({...post.images});

    return (
        <div>
            <main className='flex'>
                <section className="flex-grow pt-14 px-6">

                    <h1 className="flex text-4xl font-bold mt-2 mb-6">{post.title}</h1>
                    <div className="flex justify-between">

                        <p className="flex-items-center text-xl font-light mb-4">{post.type} | {post.bed} bed | {post.bedroom} bedrooms</p>
                    </div>
                    <div className=" flex">
                    {/* {post.images.map(item => (
                        <img className="object-cover w- rounded-3xl"  src={item}/>
                    ))}
                         */}

                        <ImageGallery className="grid grid-cols-1 object-cover w-52" items={images}/>
                    </div>


                </section>
            </main>

        </div>
    )
}

export default Details
