       title: 'CapeCoast', key: '3',
       coordinate: {latitude: 5.1324685116194315, longitude:  -1.2827509805627975}
      },

      {
        image:  'https://d5w4alzj7ppu4.cloudfront.net/cities/capecoast.jpeg',
        title: 'Takoradi', key: '4',
        coordinate: {latitude: 4.901835542413145, longitude:  -1.7819746801349148}
       },

       {
        image:  'https://mapio.net/images-p/18851391.jpg',
        title: 'Sunyani', key: '5',
        coordinate: {latitude: 7.335222407832707, longitude:  -2.3121501761230077}
       },

       {
        image:  'https://upload.wikimedia.org/wikipedia/commons/f/f1/Road%2C_Tamale.jpg',
        title: 'Tamale', key: '6',
        coordinate: {latitude: 9.404636124084117, longitude:  -0.842520035134283}
       },
]
function App() {
  const[posts, setPosts] = useState([]);
  useEffect ( () => {
    const fetchPosts = async () => {
        try{
            const postsResult = await API.graphql(
                graphqlOperation(listPosts, {

            })
            )

            setPosts(postsResult.data.listPosts.items);

        } catch (e){
            console.log(e);
        }
    }

    fetchPosts();
}, [])
  return (
    <div className="App">

      <Header/>
      <Banner/>
      <main className="max-w-7xl mx-auto px-8 sm:px-16">
        <section className="pt-6">
          <h2 className="text-4xl font-semibold pb-5">Find homes for rent in</h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3
          xl:grid-cols-3">
          {nearby?.map(({image, location, title, key}) => (
              <SmallCard
                key={key}
                img={image}
                title={title}
                location={location}
              />
          ))}
            </div>


        </section>
      </main>
    </div>
  );
}

export default App;
