import React, {useEffect, useState} from 'react';
import { useLocation } from 'react-router-dom';
import {listPosts} from '../graphql/queries'
import Amplify, { API, graphqlOperation } from 'aws-amplify'
import InfoCard from '../components/InfoCard';
function SearchPage(props) {
    const[posts, setPosts] = useState([]);
    const location = useLocation();
    const nelatitude = location.state.nelatitude;
    const swlatitude = location.state.swlatitude;
    const nelongitude = location.state.nelongitude;
    const swlongitude = location.state.swlongitude;
    useEffect ( () => {
    const fetchPosts = async () => {
        try{
            const postsResult = await API.graphql(
                graphqlOperation(listPosts, {

                    filter: {
                        and: {

                            latitude: {
                                between: [
                                    // Latitude - (0.4/2),
                                    // Latitude + (0.4/2),

                                    swlatitude,
                                    nelatitude,
                                    //viewport.southwest.lat,
                                    //viewport.northeast.lat,
                                ],
                            },
                            longitude: {
                                between: [

                                //   Longitude - (0.4/2),
                                //   Longitude + (0.4/2),
                                    //viewport.southwest.lng,
                                    //viewport.northeast.lng,

                                    swlongitude,
                                    nelongitude,
                                ],
                            }
                        }

                    }

            })
            )

            setPosts(postsResult.data.listPosts.items);

        } catch (e){
            console.log(e);
        }
    }

    fetchPosts();
}, [])


    console.log(posts);
    return (
        <div>
            <main className='flex'>
                <section className="flex-grow pt-14 px-6">
                    <p className="text-xs">{posts.length}Homes available for rent</p>
                    <h1 className="text-3xl font-semibold mt-2 mb-6">Homes in Kumasi</h1>

                <div className="hidden lg:inline-flex mb-5 space-x-4 text-gray-800 whitespace-nowrap">
                    <p className="px-4 py-2 border rounded-full cursor-pointer
                    hover:shadow-lg active:scale-95 active:bg-gray-700 transition transform
                    duration-100 ease-out">Full Home</p>

                    <p className="px-4 py-2 border rounded-full cursor-pointer
                    hover:shadow-lg active:scale-95 active:bg-gray-700 transition transform
                    duration-100 ease-out">Self-Contained</p>

                    <p className="px-4 py-2 border rounded-full cursor-pointer
                    hover:shadow-lg active:scale-95 active:bg-gray-700 transition transform
                    duration-100 ease-out">Single Room</p>
                </div>
                <div className="flex flex-col">

                {posts.map((item) => {
                    <InfoCard
                    key={item.id}
                    image={item.image}
                    latitude={item.latitude}
                    longitude={longitude}
                    newPrice={newPrice}
                    bed={bed}
                    description={description}
                    title={title}
                     />
                })}

                </div>
                </section>
            </main>
        </div>
    )
}

export default SearchPage
