import React, {useState, useEffect} from 'react';
import { useLocation } from 'react-router-dom';
import {listPosts} from '../graphql/queries'
import Amplify, { API, graphqlOperation } from 'aws-amplify'
import Carousel from 'react-gallery-carousel';
import 'react-gallery-carousel/dist/index.css';

function Details() {
    const location = useLocation();
    const homeid = location.state.homeid;
    const [posts, setPosts] = useState();
    const [months, setMonths] = useState(0);
    const [years, setYears] = useState(0);

    useEffect ( () => {
        const fetchPosts = async () => {
            try{
                const postsResult = await API.graphql(
                    graphqlOperation(listPosts)
                )

                setPosts(postsResult.data.listPosts.items);
            } catch (e){
                console.log(e);
            }
        }

        fetchPosts();
    }, [])
    if(posts === undefined){
        return null;
    }

    const post = posts.find(place => place.id === homeid);
    const amount = post.newPrice;
    const images = post.images.map((item, index) => ({
        src: item,
        index: index
      }));
      console.log(images);
    return (
        <div>
            <main className='flex'>
                <section className="flex-grow pt-14 px-6">

                    <h1 className="flex text-4xl font-bold mt-2 mb-6">{post.title}</h1>
                    <div className="flex justify-between">

                        <p className="flex-items-center text-xl font-light mb-4">{post.type} | {post.bed} bed | {post.bedroom} bedrooms</p>
                    </div>
                    <div className="flex justify-between">
                        <div className="mb-4 rounded-xl">
                        <Carousel
                        className="mb-4"
                        isLoop={true}
                        isAutoPlaying={true}
                        canAutoPlay={true} 
                        hasThumbnails={false}
                        images={images}
                        style={{ height: 500, width: 700 }}
                            /> 
                        </div>
                <div className="bg-white opacity-100 bg-opacity-100 shadow-2xl h-full">
                <div class="px-4 py-4 mb-6 max-w-sm rounded-2xl bg-opacity-100 bg-white  overflow-hidden shadow-2xl">
                    <div class="px-6 py-4">
                        <div class="flex font-bold text-xl mb-2 mt-2">GH₵ {post.newPrice} / year</div>
                        <div className=" border-b w-100 mb-2"> </div>
                        <p class="mb-10 flex justify-between text-gray-700 text-base">
                            <div>
                                <p className="flex text-md mr-4 font-semibold"> How many years</p>
                            </div>
                            <div className="flex justify-between">
                                <p className="px-4 ml-4 flex text-md font-semibold rounded-full  bg-pink-white border-2 border-black">-</p>
                                <p className="px-4">{years}</p>
                                <p className="px-4 flex text-md font-semibold rounded-full  bg-pink-white border-2 border-black">+</p>
                            </div>

                        </p>
                        <div className=" border-b w-100 mb-2 mt-2"> </div>
                        <p class="mb-10 flex justify-between text-gray-700 text-base">
                            <div>
                                <p className="flex text-md mr-4 font-semibold"> How many months</p>
                            </div>
                            <div className="flex justify-between">
                                <p className="px-4 ml-4 flex text-md font-semibold rounded-full  bg-pink-white border-2 border-black">-</p>
                                <p className="px-4">{months}</p>
                                <p className="px-4 flex text-md font-semibold rounded-full  bg-pink-white border-2 border-black">+</p>
                            </div>

                        </p>
                        <div className=" border-b w-100 mb-2"> </div>
                    </div>

                    <div className="flex justify-between px-4 py-4 mb-10 border w-full border-black rounded-2xl max-w-full">
                        <div>
                            <p>Years</p>
                            <p>Months</p>
                            <p>Total</p>
                        </div>
                        <div>
                        <p>{years}</p>
                        <p>{months}</p>
                        <p>GH₵{Math.round(amount*(years+(months/12)))}</p>
                            </div>
                    </div>

                    <div class="px-6 pt-4 pb-2">
                        <button className=" bg-pink-600 w-full text-xl font-semibold px-2 py-3 rounded-xl bg-blend-color text-white">Pay to Rent</button>
                    </div>
                    </div>
                </div>

                    </div>


                </section>
            </main>

        </div>
    )
}

export default Details
